@import url('https://fonts.googleapis.com/css2?family=Montserrat&family=Nunito+Sans&family=Poppins:wght@300;400;500;600;700;800;900&display=swap');

* {
    margin: 0;
    padding: 0;
    box-sizing:border-box;
    font-family: "Poppins", sans-serif;
}

body section {
    background-color: #154c79;
}

.login {
    color: #FFF;
    background: url(/1f6bbc4dd0c3fd815c96a8c9aa60457c.jpg) no-repeat;
    position: relative;
    width: 100%;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-size:cover;
    background-position: center;
}

section {
    position: relative;
    width: 100%;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    background: url(/7fd14eea05f86db4f7ec4194ecd2f564.jpg)no-repeat;
    background-color: #154c79;
    background-size: cover;
    background-position: center;
    color: #fff;
}

.about-section{
    position: relative;
    width: 100%;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    background: url(/110bc797234b6bb3b5d6dce8b1b19265.jpg)no-repeat;
    background-size: cover;
    background-position: center;
}
header {
    position: relative;
    top: 0;
    width: 100%;
    padding: 30px 100px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

header .logo {
    position: relative;
    color: #000;
    font-size: 30px;
    text-decoration: none;
    text-transform: uppercase;
    font-weight: 800;
    letter-spacing: 1px;
}

header .navigation a {
    color: #fff;
    text-decoration: none;
    font-weight: 500;
    letter-spacing: 1px;
    padding: 2px 15px;
    border-radius: 20px;
    transition: 0.3s;
    transition-property: all;
}

header .navigation a:not(:last-child) {
    margin-right: 30px; 
}

header .navigation a:hover {
    background: #0C4F60;

}

.content {
    max-width: 650px;
    margin: 60px 100px;
}

.content .info h2{
    color: #226A80;
    font-size: 55px;
    text-transform: uppercase;
    font-weight: 800;
    letter-spacing: 2px;
    line-height: 60px;
    margin-bottom: 30px;
}

.content .info h2 span {
    color: #fff;
    font-size: 50px;
    font-weight: 600;
}

.content .info p {
    font-size: 16px;
    font-weight: 500;
    margin-bottom: 40px;
    color: #fff;
}

.content .info-btn {
    color: #fff;
    background: #226A80;
    text-decoration: none;
    text-transform: uppercase;
    font-weight: 700;
    letter-spacing: 2px;
    padding: 10px 20px;
    border-radius: 5px;
}

.content .info-btn:hover {
    background: #0C4F60;
}

.media-icons {
    display: flex;
    justify-content: center;
    align-items: center;
    margin: auto;
    color: #fff;
}

.media-icons a {
    position: relative;
    color: #fff;
    font-size: 35px;
    transition: 0.3s;
    transition-property: all;
}

.media-icons a:not(:last-child) {
    margin-right: 60px;
}

.media-icons a:hover {
    transform: scale(1.4);
}

label {
    display: none;
}

#check {
    z-index: 3;
    display: none;
}

.content .info img {
    height: 100px;
    width: 100px;
    align-items:baseline;
    border-radius: 50%;
}


/* Responsive styles */
@media (max-width: 960px) {
    header .navigation{
        display: none;
    }
    label {
        display: block;
        font-size: 25px;   
        cursor: pointer;
        transition: 0.3s;
        transition-property: color;
    }

    label:hover {
        color: #fff;
    }

    label .close-btn {
        display: none;
    }
    #check:checked ~ header .navigation {
        z-index: 2;
        position: fixed;
        background: rgba(255, 246, 114, 0.9);
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
    }

    #check:checked ~ header .navigation a {
        font-weight: 700;
        margin-right: 0;
        margin-bottom: 50px;
        letter-spacing: 2px;
    }

    #check:checked ~ header label .menu-btn {
        display: none;
    }

    #check:checked ~ header label .close-btn {
        z-index: 2;
        display: block;
        position: fixed;
    }

    label .menu-btn {
        position: absolute;
    }

    header .logo {
        position: absolute;
        bottom: 6px;
    }

    .content .info h2{
        font-size: 45px;
        line-height: 50px;
    }


    .content .info h2 span {
        font-size: 40px;
        font-weight: 600;
    } 

    content .info p {
        font-size: 14px;
    }
}

@media (max-width: 560px) {
    .content .info h2{
        font-size: 35px;
        line-height: 40px;
    }

    .content .info h2 span {
        font-size: 30px;
        font-weight: 600;
    } 

    content .info p {
        font-size: 14px;
    }
}
